    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        try{
        base.level=index+1;
        base b=new base();
        b.setVisible(true);
        dispose();
        }catch(Exception e)
        {
            
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jComboBox2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox2ActionPerformed

        try
        {           
        base.s=jComboBox2.getSelectedItem().toString();
        FileInputStream fin=new FileInputStream(base.s+".au");
        base.first=new AudioStream(fin);
        }
        catch(Exception e)
        {}
    }//GEN-LAST:event_jComboBox2ActionPerformed

    private void jComboBox3ItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_jComboBox3ItemStateChanged

        String temp=jComboBox3.getSelectedItem().toString();
        if(temp.equals("ON"))
            base.game_sound=true;
        else
            base.game_sound=false;
    }//GEN-LAST:event_jComboBox3ItemStateChanged

